#include <iostream>using namespace std;/*The cout object, together with the << operator, is used to output values/print textTo insert a new line, you can use the \n character:Another way to insert a new line, is with the endl manipulator*///A single line comment/* A block comment */int main(){    cout << "!!!Hello World!!!" << endl; // It prints Hello World    cout << "welcome to the world of C++ \n";    cout << "One of the popular OOP langauge" << endl;    cout << "Lets learn it";	return 0;}
